<?php
 class Zend_Amf_Value_Messaging_AbstractMessage { public $clientId; public $destination; public $messageId; public $timestamp; public $timeToLive; public $headers; public $body; public function generateId() { return sprintf( '%08X-%04X-%04X-%02X%02X-%012X', mt_rand(), mt_rand(0, 65535), bindec(substr_replace( sprintf('%016b', mt_rand(0, 65535)), '0100', 11, 4) ), bindec(substr_replace(sprintf('%08b', mt_rand(0, 255)), '01', 5, 2)), mt_rand(0, 255), mt_rand() ); } } 